import React, { useEffect, useState } from 'react';
import { Api } from '../utils/Api';
import './PlayerScreen.css'; // Import CSS file for styling

function PlayerScreen() {
  const [players, setPlayers] = useState([]);
  const [loading,setLoading] = useState(true)

  useEffect(() => {
    const fetchPlayers = async () => {
      try {
        const { data } = await Api.getRequest('/api/players');
        const parsedData = JSON.parse(data);
        setPlayers(parsedData);
        setLoading(false)
      } catch (error) {
        console.error('Error fetching players:', error);
        setError('Error fetching players. Please try again.'); // Set error message
        setLoading(false);
      }
    };

    fetchPlayers();
  }, []);

  if(players.length);

  return (
    <div className="player-container">
      {players.map((player, index) => (
        <div key={index} className="player-card">
          <div className="player-image">
            <img src={player.imageUrl} alt="Player" />
          </div>
          <div className="player-info">
            <div className="info-row">
              <strong>Email:</strong> {player.email}
            </div>
            <div className="info-row">
              <strong>Name:</strong> {player.firstname} {player.lastname}
            </div>
            <div className="info-row">
              <strong>Phone:</strong> {player.phone}
            </div>
            <div className="info-row">
              <strong>Sports:</strong> {player.sports}
            </div>
            <div className="info-row">
              <strong>Price:</strong> {player.price}
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default PlayerScreen;
