import './HomeScreen.css';
import { useEffect, useState } from 'react';
import { Api } from '../utils/Api';
import Players from '../components/Players';

const PlayerScreen = () => {
  const [players, setPlayers] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPlayers = async () => {
      try {
        const { data } = await Api.getRequest('/api/players');
        const parsedData = JSON.parse(data);
        setPlayers(parsedData[0]);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching players:', error);
        setError('Error fetching players. Please try again.');
        setLoading(false);
      }
    };

    fetchPlayers();
  }, []);

  return (
    <div className="homescreen">
      <h2 className="homescreen__title">Latest Products</h2>
      <div className="homescreen__products">
        {loading ? (
          <h2>Loading...</h2>
        ) : error ? (
          <h2>{error}</h2>
        ) : (
          Object.keys(players).map(playerKey => (
            <Players
              key={playerKey}
              name={players[playerKey].lastname}
              description={players[playerKey].description}
              price={players[playerKey].price}
              imageUrl={players[playerKey].imageUrl}
              productId={players[playerKey]._id}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default PlayerScreen;
