import React, { useEffect, useState } from 'react';
import { Api } from '../utils/Api';
import './Dashboard.css'

function AdminDashboard() {
  const [users, setUsers] = useState([]);
  const [players, setPlayers] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const { data } = await Api.getRequest('/api/user/all');
        setUsers(JSON.parse(data));
        const playerData  = await Api.getRequest('/api/players/');
        setPlayers(JSON.parse(playerData.data));
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);
  console.log(players[0]);
  console.log(users[0]);

  return (
    <div className="table-container " style={{marginLeft: '5%', marginRight:'5%'} }>
    <h2 style={{marginBottom:'40px'}}>Users</h2>
      <table>
        <thead>
          <tr>
            <th>Email</th>
            <th>Full Name</th>
          </tr>
        </thead>
        <tbody>
        {Object.keys(users).map((key, index) => (
            <tr key={index}>
              <td>{users[key].email}</td>
              <td>{users[key].fullName}</td>
            </tr>
          ))}
        </tbody>
      </table>
         <h2 style={{marginTop:'40px' ,marginBottom:'40px'}}>Players</h2>
      <table>
        <thead>
          <tr>
            <th>Email</th>
            <th>Name</th>
            <th>Phone</th>
            <th>Price</th>
            <th>Sport</th>
            <th>Status</th>


       </tr>
       </thead>
        <tbody>
          {Object.keys(players).map((key, index) => (
            <tr key={index}>
            <td>{players[key].email}</td>
            <td>{players[key].firstname}{" "}{players[key].lastname}</td>
            <td>{players[key].phone}</td>
            <td>{players[key].price}</td>
            <td>{players[key].sports}</td>
            <td>{players[key].status}</td>


          </tr>
        ))}
        </tbody>
      </table>
    </div>

    
  );
}

export default AdminDashboard;
