import React, { useEffect, useState } from 'react';
import { Api } from '../utils/Api';
import { useSelector } from 'react-redux';

function OfferScreen() {
    const user = useSelector(state => state.user);
    const playerId = user.userInfo.details._id;
    const [offers, setOffers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(false);

    useEffect(() => {
        const fetchOffers = async () => {
            try {
                const data = await Api.getRequest(`/api/auction/bid/${playerId}`);
                setOffers(data); 
                setLoading(false);
            } catch (error) {
                console.error('Error fetching offers:', error);
                setError('Error fetching offers. Please try again.'); 
                setLoading(false);
            }
        };

        fetchOffers();
    }, [playerId]);
    console.log(offers);
    return (
        <div>
            {/* {loading ? (
                <h2>Loading...</h2>
            ) : error ? (
                <h2>{error}</h2>
            ) : (
                <div>
                    <h2>Offers:</h2>
                    <ul>
                        {offers.map((offer, index) => (
                            <li key={index}>
                                <p>Player ID: {offer.playerId}</p>
                                <p>Description: {offer.description}</p>
                                <p>Current Bid: {offer.currentBid}</p>
                                {/* Add more details as needed */}
                            {/* </li>
                        ))}
                    </ul>
                </div>
            )} */} 
        </div>
    );
}

export default OfferScreen;
