import React, { useCallback, useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { Api } from '../../utils/Api';
import './index.css';

function Index() {
  const { replace, push } = useHistory();

  const [firstname, setFirstName] = useState('');
  const [lastname, setLastName] = useState('');
  const [phone, setPhone] = useState('');
  const [price, setPrice] = useState('');
  const [sports, setSports] = useState('');
  const [description, setDescription] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [imageFile, setImageFile] = useState(null); // State for storing the uploaded image file
  const [imageString, setImageString] = useState(''); // State for storing the base64 string of the image
  const [loading, setLoading] = useState(false);
  const status = 1;

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImageFile(file);
    // Convert image file to base64 string
    const reader = new FileReader();
    reader.onloadend = () => {
      setImageString(reader.result);
    };
    reader.readAsDataURL(file);
  };

  const _handleSubmit = useCallback(async () => {
    if (
      firstname.length > 2 &&
      sports.length > 2 &&
      description.length > 2 &&
      lastname.length > 2 &&
      phone.length > 2 &&
      email.length > 2 &&
      password.length > 2 &&
      price.length > 2 &&
      imageString // Ensure an image is uploaded and converted to base64 string
    ) {
      setLoading(true);
      const { statusCode, data } = await Api.postRequest(
        '/api/players/registerPlayer',
        {
          email,
          firstname,
          lastname,
          phone,
          sports,
          status,
          description,
          price,
          password,
          imageUrl: imageString, // Include base64 string of the image in the request body
        }
      );
      if (statusCode === 400 || statusCode === 500 || statusCode === 403) {
        setLoading(false);
        alert(data);
        return;
      }
      alert(data);
      replace('/signin');
    }
  }, [
    email,
    firstname,
    lastname,
    phone,
    password,
    sports,
    description,
    price,
    imageString,
    replace,
  ]);

  if (loading) return <h1>Loading...</h1>;

  return (
    <div className="signupscreen">
      <div className="container">
        <div className="innerContainer">
          <div
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: '20px',
              // backgroundColor: 'red',
            }}
          >
            <div style={{ cursor: 'pointer' }} onClick={() => push('/')}>
              <i className="fas fa-arrow-circle-left fa-5x"></i>
            </div>
            <p>Signup</p>
          </div>

          <label htmlFor="fname">First Name</label>
          <input
            type="text"
            id="fname"
            name="firstname"
            placeholder="Your full name.."
            value={firstname}
            onChange={(e) => setFirstName(e.target.value)}
          />
          <label htmlFor="fname">Last Name</label>
          <input
            type="text"
            id="lastname"
            name="lastname"
            placeholder="Your full name.."
            value={lastname}
            onChange={(e) => setLastName(e.target.value)}
          />

          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="lname"
            name="email"
            placeholder="Your email.."
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <label htmlFor="phone">Phone</label>
          <input
            type="text"
            id="phone"
            name="phone"
            placeholder="Your full name.."
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
          <label htmlFor="fname">Sports</label>
          <select onChange={(e) => setSports(e.target.value)}>
            <option>FootBall</option>
            <option>Cricket</option>
          </select>
          <label htmlFor="fname">Price</label>
          <input
            type="text"
            id="price"
            name="price"
            placeholder="Your Price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
          <label htmlFor="image">Upload Image</label>
          <input
            type="file"
            id="image"
            name="image"
            accept="image/*"
            onChange={handleImageChange}
          /><br></br>
          <label htmlFor="fname">Description</label>
          <textarea
            onChange={(e) => setDescription(e.target.value)}
            id="description"
          ></textarea>
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="lname"
            name="password"
            placeholder="Your Password.."
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />

          {/* Image uploader */}
          

          <Link to="/signin" className="link">
            <span>Already have an account ?</span>
          </Link>
          <br />

          <input type="submit" value="Sign up" onClick={_handleSubmit} />
        </div>
      </div>
    </div>
  );
}

export default Index;
