import './ProductScreen.css'
import React, { useEffect, useState } from 'react';
import { Api } from '../utils/Api';
import Players from '../components/Players'
import './PlayerScreen.css'; // Import CSS file for styling

function PlayerScreen() {
  const [players, setPlayers] = useState([]);
  const [loading,setLoading] = useState(true)
  const [error,setError] = useState(false)

  useEffect(() => {
    const fetchPlayers = async () => {
      try {
        const { data } = await Api.getRequest('/api/players');
        const parsedData = JSON.parse(data);
        setPlayers(parsedData);
        setLoading(false)
      } catch (error) {
        console.error('Error fetching players:', error);
        setError('Error fetching players. Please try again.'); 
        setLoading(false);
      }
    };

    fetchPlayers();
  }, []);
  console.log(players);

  return (
    <div className="productscreen">
      {loading ? (
        <h2>Loading...</h2>
      ) : error ? (
        <h2>{error}</h2>
      ) : (
        <>
          <div className="productscreen__left">
            <div className="left__image">
              {/* <img src={product.imageUrl} alt={product.name} /> */}
            </div>
            <div className="left__info">
              <p className="left__name">{players.firstname}{" "}{players.lastname}</p>
              <p>Price: ${players.price}</p>
              <p>Description: {players.description}</p>
            </div>
          </div>
          <div className="productscreen__right">
            <div className="right__info">
              <p>
                Price:
                <span>${players.price}</span>
              </p>
              <p>
                Status:
                <span>
                  {players.s > 0 ? 'In Stock' : 'Out of Stock'}
                </span>
              </p>
              
            </div>
          </div>
        </>
      )}
    </div>
  )
}

export default PlayerViewScreen
